# =[ FONCTIONS SCRIPTS ]============================================================================
# Fonction pour afficher l'animation de chargement
loading_animation()
{
    local pid=$1
    local delay=$2
    shift 2
    local frames=("${@}")

    while kill -0 $pid 2>/dev/null; do  # Continue tant que le processus est en cours
        for frame in "${frames[@]}"; do
            echo -ne "\r$frame"
            sleep $delay
        done
    done
    printf "\r"  # Efface l'animation √† la fin
}

# Fonction pour ex√©cuter une commande avec animation
run_with_loading()
{
    "$@" &  # Ex√©cute la commande en arri√®re-plan
    local pid=$!
    loading_animation ${pid} 0.2 ${b_big_ball[@]}
    wait $pid
}



# ==================================================================================================
# MAIN
# ==================================================================================================
run_with_loading sleep 10
